// Store CO2 reading from radio gateway
const co2Value = parseFloat(msg.payload);
if (!isNaN(co2Value)) {
    global.set('radioCO2', co2Value);
    global.set('radioCO2Timestamp', new Date());
    node.warn(`ðŸ“¡ Radio CO2: ${co2Value} ppm`);

    // Trigger data sending after CO2 reading
    msg.trigger = 'radio-co2';
    return msg;
}
return null;

// Store device ID from radio gateway
let deviceId = msg.payload;

// Handle different formats
if (typeof deviceId === 'string') {
    deviceId = deviceId.replace(/["/]/g, ''); // Remove quotes and slashes
} else {
    deviceId = deviceId.toString();
}

if (deviceId && deviceId.length > 0) {
    global.set('radioDeviceId', deviceId);
    node.warn(`ðŸ“¡ Radio Device ID: ${deviceId}`);
}
return null;

// Store battery level from radio gateway
const batteryLevel = parseFloat(msg.payload);
if (!isNaN(batteryLevel)) {
    global.set('radioBattery', batteryLevel);
    node.warn(`ðŸ“¡ Radio Battery: ${batteryLevel}%`);
}
return null;

// Get all stored radio values
const co2Level = global.get('radioCO2');
const deviceId = global.get('radioDeviceId');
const batteryLevel = global.get('radioBattery') || 100;
const timestamp = global.get('radioCO2Timestamp') || new Date();

// Only send if we have both CO2 and device ID
if (co2Level !== undefined && deviceId) {

    // Create payload for backend
    msg.payload = {
        deviceId: deviceId,
        co2_ppm: co2Level,
        battery_percent: batteryLevel,
        timestamp: timestamp.toISOString()
    };

    // Set backend URL
    msg.url = "https://iotgrp2-ooor.onrender.com/api/readings/gateway";

    node.warn(`ðŸ“¡ Sending radio data to backend: ${JSON.stringify(msg.payload)}`);

    return msg;
} else {
    node.warn(`ðŸ“¡ Missing radio data - CO2: ${co2Level}, DeviceID: ${deviceId}`);
    return null;
}

// Check if the API call was successful
const statusCode = msg.statusCode;

if (statusCode >= 200 && statusCode < 300) {
    // Success
    node.status({fill:"green", shape:"dot", text:"Radio Success"});
    node.warn(`âœ… Radio data sent successfully! CO2: ${global.get('radioCO2')} ppm`);
} else {
    // Error
    node.status({fill:"red", shape:"ring", text:"Radio Error: " + statusCode});
    node.error(`âŒ Failed to send radio data: ${statusCode} - ${JSON.stringify(msg.payload)}`);
}

return msg;
